-- Records of sb_admin
-- ----------------------------
INSERT INTO `sb_admin` VALUES (1, 'admin', '超级管理员', 'd9ee6aeebdef81c5d6b0c4c51c03d967', '67859c', '/storage/uploads/202110/ec9e544f672a85ff7d2a30cabe5792f5.gif', 'admin@admin.com', 0, '2021-09-26 10:26:22', '127.0.0.1', '2021-08-16 08:31:24', NULL, '7ff74c19d284a4d8583e8c9aee2e2442', 1, 1000);

-- ----------------------------
-- Table structure for sb_article
-- ----------------------------
DROP TABLE IF EXISTS `sb_article`;
CREATE TABLE `sb_article`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `token` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '文章token',
  `cid` smallint(6) UNSIGNED NOT NULL DEFAULT 0 COMMENT '分类id',
  `user_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '用户id',
  `title` varchar(60) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '标题',
  `desc` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '摘要',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '内容',
  `like` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '喜欢',
  `read` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '阅读',
  `attr` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '属性：0普通文章，1单页',
  `status` tinyint(1) NOT NULL DEFAULT 0 COMMENT '状态:0-关闭，1打开',
  `is_top` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '置顶',
  `is_nice` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '推荐',
  `is_index` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '首页',
  `sort` int(10) NOT NULL DEFAULT 1000 COMMENT '排序',
  `createtime` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `updatetime` datetime NULL DEFAULT NULL COMMENT '更新时间',
  `state` tinyint(1) UNSIGNED NOT NULL DEFAULT 2 COMMENT '1:草稿箱 2:发布',
  `thumb` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '缩略图',
  `thumb_ext` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '多图',
  `seo_title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT 'seo标题',
  `seo_desc` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT 'seo描述',
  `seo_key` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT 'seo关键词',
  `is_del` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '删除:0未删，1已删',
  `path` varchar(60) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '路径',
  PRIMARY KEY (`id`, `token`) USING BTREE,
  UNIQUE INDEX `path`(`path`) USING BTREE,
  INDEX `cid`(`cid`) USING BTREE,
  INDEX `title`(`title`) USING BTREE,
  INDEX `token`(`token`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 33 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '文章' ROW_FORMAT = DYNAMIC;

