-- ----------------------------
-- Table structure for sb_attachment
-- ----------------------------
DROP TABLE IF EXISTS `sb_attachment`;
CREATE TABLE `sb_attachment`  (
  `id` int(20) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '附件id',
  `user_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '上传者',
  `url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '附件路径',
  `filesize` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '附件大小',
  `width` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '宽度',
  `height` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '高度',
  `createtime` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `desc` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '附件描述',
  `mold` enum('other','picture','video','document','excel','music') CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'other' COMMENT 'other其他\r\npicture图片\r\nvideo视频\r\ndocument文档',
  `updatetime` datetime NULL DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 29 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '附件表' ROW_FORMAT = DYNAMIC;

