-- ----------------------------
-- Table structure for sb_auth_rule
-- ----------------------------
DROP TABLE IF EXISTS `sb_auth_rule`;
CREATE TABLE `sb_auth_rule`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '菜单id',
  `pid` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '父id',
  `url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '地址',
  `title` varchar(60) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '标题',
  `icon` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '图标',
  `component` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '引入组件',
  `status` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '状态：0隐藏，1展示',
  `remark` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '简述',
  `createtime` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `updatetime` datetime NULL DEFAULT NULL COMMENT '更新时间',
  `redirect` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '跳转url',
  `sort` int(10) NOT NULL DEFAULT 1000 COMMENT '排序',
  `is_menu` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '状态：0非菜单，1菜单',
  `is_not_del` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '不可删除类型',
  `has_children` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否拥有子菜单',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `pid`(`pid`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 25 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '权限菜单' ROW_FORMAT = DYNAMIC;

